mapper.select:
SELECT A.SCHDUL_ID, A.SCHDUL_SE, A.SCHDUL_DEPT_ID, A.SCHDUL_KND_CODE, TO_CHAR(A.SCHDUL_BEGINDE, 'YYYYMMDDHH24MI')SCHDUL_BGNDE, TO_CHAR(A.SCHDUL_ENDDE, 'YYYYMMDDHH24MI')SCHDUL_ENDDE, A.SCHDUL_NM, A.SCHDUL_CN, A.SCHDUL_PLACE, A.SCHDUL_IPCR_CODE, A.SCHDUL_CHARGER_ID, A.ATCH_FILE_ID, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD') FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'YYYY-MM-DD') LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.REPTIT_SE_CODE FROM LETTNSCHDULINFO A WHERE 1=1
AND SCHDUL_SE LIKE CONCAT(CONCAT('%', #{searchKeyword}), '%')
ORDER BY A.FRST_REGIST_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
